package day_05;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Example1");
		int i=1;
		do{
		System.out.println(i);
		i++;
		}
		while(i<=10);
		System.out.println("Example2");
		int j=10;
		do{
		System.out.println(j);
		j--;
		}
		while(j>10);
	}

}
