package day_08;

public class EqualsMethod{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name1="nikita";
		String name2="nikita";
		System.out.println(name1==name2);
		System.out.println(name1.equals(name2));
		System.out.println(name1.equals("Nikita"));
	}

}
